mapper.insert:
INSERT INTO MEMBER(MEMBER_ID
				 , MEMBER_PWD
				 , MEMBER_NICK
				 , POST
				 , BASE_ADDR
				 , EXTRA_ADDR
				 , BIRTH
				 , PHONE
				 , MEMBER_INTRO
				 , MEMBER_AUTH
				 , ENROLL_DATE
				 , LAST_LOGIN)
VALUES(#{memberId}
	 , #{memberPwd}
	 , #{memberNick}
	 , #{post}
	 , #{baseAddr}
	 , #{extraAddr}
	 , #{birth}
	 , #{phone}
	 , #{memberIntro}
	 , '일반'
	 , DEFAULT
	 , NULL)

mapper.select:
	SELECT * 
   	  FROM MEMBER
	 WHERE MEMBER_ID = #{memberId}
	   AND MEMBER_PWD = #{memberPwd}

mapper.update:
UPDATE MEMBER
   SET LAST_LOGIN = SYSDATE
 WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICK = #{nick}
